return-response.set-body:
@{
                    var operation = context.Request.MatchedParameters["operation"];
                    var a = double.Parse(context.Request.MatchedParameters["a"]);
                    var b = double.Parse(context.Request.MatchedParameters["b"]);
                    double result;
                    switch (operation.ToLower())
                    {
                        case "add":
                            result = a + b;
                            break;
                        case "subtract":
                            result = a - b;
                            break;
                        case "multiply":
                            result = a * b;
                            break;
                        case "divide":
                            result = b != 0 ? Math.Round(a / b, 4) : 0;
                            break;
                        default:
                            result = 0;
                            break;
                    }
                    return new JObject(
                        new JProperty("operation", operation),
                        new JProperty("a", a),
                        new JProperty("b", b),
                        new JProperty("result", result)
                        ).ToString();
                    }
